def get_SplitLists(BU_list, splitting_indices):
    """
    splitting_indices : list of burnup points at which the original burnup list is split to allow for fitting on different intervals
    This function returns the burnup points list, as well as the split lists for the fitting
    """
    ################################## HOM_CELL_study :
    if splitting_indices == []:
        return [BU_list]
    else:
        indices = sorted(splitting_indices)
        
        # Add the start and end of the list to indices for easy splitting
        indices = [0] + indices + [len(BU_list)]
        
        # Split the list into sub-lists based on the indices
        sub_lists = [BU_list[indices[i]:indices[i+1]] for i in range(len(indices)-1)]
    
        return sub_lists

    
def exclude_points(burnup_points, excluded_points):
    """
    burnup_points : list of burnup points
    excluded_points : list of burnup points to exclude
    This function returns the burnup points list without the excluded points
    """
    for point in excluded_points:
        burnup_points.remove(point)
    return burnup_points


def convert_Serpent_to_UTL(BU_Serpent_list):
    """
    Convert D5 units of [MWd/tU] to UTL units of [MWd/kgU]
    """
    BU_Serpent_list = np.array(BU_Serpent_list)
    BU_Dragon_list = 1000*BU_Serpent_list
    return BU_Dragon_list

def print_in_S2_format(BU_list):
    """
    Print the burnup points list in Serpent-2 format
    """
    for BU in BU_list:
        print(f"{BU:.4f}")


def convert_BU_to_days(BU_list, specific_power):
    """
    BU_list : list of burnup points, [MWd/tU]
    specific_power : specific power of the fuel assembly [W/gU] = [MW/MgU] = [MW/tU]
    This function returns the burnup points list in days
    """
    BU_days_list = [BU/specific_power for BU in BU_list]
    return BU_days_list


